				FROM ${board} B
				LEFT OUTER JOIN ${member} M ON B.${memberNumber} = M.${memberNumber}
				WHERE B.${boardSection} = #{boardSection} AND B.${boardList} = #{boardList}				
				ORDER BY B.${boardNumber} DESC
